import React from 'react';
import Form from './components/Form';
import Card from './components/Card';
import Filters from './components/Filters';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      cardName: '',
      cardDescription: '',
      cardAttr1: '',
      cardAttr2: '',
      cardAttr3: '',
      cardImage: '',
      cardRare: 'normal',
      cardTrunfo: false,
      hasTrunfo: false,
      isSaveButtonDisabled: true,
      deck: [],
      filter: '',
      rarityFilter: 'todas',

    };
  }

  onSaveButtonClick= () => {
    const {
      deck,
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
    } = this.state;

    const savedCard = {
      deck,
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
    };

    this.setState(() => ({
      cardName: '',
      cardDescription: '',
      cardAttr1: '0',
      cardAttr2: '0',
      cardAttr3: '0',
      cardImage: '',
      cardRare: 'normal',
      cardTrunfo: false,
      hasTrunfo: cardTrunfo === true,
      deck: deck.concat(savedCard),
    }));

    if (cardTrunfo) {
      this.setState({ hasTrunfo: true });
    }
  }

  checkButton = () => {
    const
      {
        cardName,
        cardDescription,
        cardImage,
        cardAttr1,
        cardAttr2,
        cardAttr3,
      } = this.state;

    let result = false;

    if (
      cardName.length === 0
      || cardDescription.length === 0
      || cardImage.length === 0
    ) {
      result = true;
    }

    const total = 210;
    if (Number(cardAttr1) + Number(cardAttr2) + Number(cardAttr3) > total) {
      result = true;
    }

    const max = 90;
    if (Number(cardAttr1) > max
        || Number(cardAttr2) > max
        || Number(cardAttr3) > max) {
      result = true;
    }

    if (Number(cardAttr1) < 0
        || Number(cardAttr2 < 0)
        || Number(cardAttr3 < 0)) {
      result = true;
    }

    this.setState({ isSaveButtonDisabled: result });
  }

  onInputChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState(() => ({
      [name]: value }), () => {
      this.checkButton();
    });
  }

  // ref (https://newbedev.com/javascript-how-to-add-delete-button-in-react-js-code-example)
  delete(card) {
    const { deck } = this.state;
    const alou = deck.filter((c) => c.cardName !== card.cardName);
    this.setState({ deck: alou });
    const trunfo = alou.filter((c) => c.cardTrunfo);
    if (trunfo) this.setState({ hasTrunfo: false });
  }

  filterByName = ({ target }) => this.setState({ filter: target.value });

  filterByRarity = ({ target }) => this.setState({ rarityFilter: target.value });

  render() {
    const { deck, filter, rarityFilter } = this.state;
    return (
      <div>
        <h1>Tryunfo</h1>
        <Form
          { ... this.state }
          onSaveButtonClick={ this.onSaveButtonClick }
          onInputChange={ this.onInputChange }

        />

        <div className="card">
          <Card
            { ... this.state }
          />

          <Filters
            filterByName={ this.filterByName }
            filterByRarity={ this.filterByRarity }
          />

          {deck.filter((card) => card.cardName.includes(filter))
            .filter((card) => {
              if (rarityFilter === 'normal') {
                return card.cardRare === 'normal';
              }
              if (rarityFilter === 'raro') {
                return card.cardRare === 'raro';
              }
              if (rarityFilter === 'muito raro') {
                return card.cardRare === 'muito raro';
              }
              return card;
            }).map((card) => (
              <div key={ card.cardName }>
                <Card
                  { ... card }
                />
                <button
                  type="button"
                  data-testid="delete-button"
                  onClick={ this.delete.bind(this, card) }
                >
                  exluir
                </button>
              </div>))}

        </div>
      </div>
    );
  }
}

export default App;
