import React from 'react';
import Form from './components/Form';
import Card from './components/Card';
import Filters from './components/FIlters';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      cardName: '',
      cardDescription: '',
      cardAttr1: '',
      cardAttr2: '',
      cardAttr3: '',
      cardImage: '',
      cardRare: 'normal',
      cardTrunfo: false,
      hasTrunfo: false,
      isSaveButtonDisabled: true,
      deck: [],
      filterName: '',
      filterRarity: 'todas',
      filterCheckbox: false,

    };
  }



  onSaveButtonClick= () => {
    const {
      deck,
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
    } = this.state;

    /*   const isTrunfo = cardTrunfo === true; */

    const savedCard = {
      deck,
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
    };

    this.setState(() => ({
      cardName: '',
      cardDescription: '',
      cardAttr1: '0',
      cardAttr2: '0',
      cardAttr3: '0',
      cardImage: '',
      cardRare: 'normal',
      cardTrunfo: false,
      hasTrunfo: cardTrunfo === true,
      deck: deck.concat(savedCard),
    }));

    if (cardTrunfo) {
      this.setState({ hasTrunfo: true });
    }
  }

  checkButton = () => {
    const
      {
        cardName,
        cardDescription,
        cardImage,
        cardAttr1,
        cardAttr2,
        cardAttr3,
      } = this.state;

    let result = false;

    if (
      cardName.length === 0
      || cardDescription.length === 0
      || cardImage.length === 0
    ) {
      result = true;
    }

    const total = 210;
    if (Number(cardAttr1) + Number(cardAttr2) + Number(cardAttr3) > total) {
      result = true;
    }

    const max = 90;
    if (Number(cardAttr1) > max
        || Number(cardAttr2) > max
        || Number(cardAttr3) > max) {
      result = true;
    }

    if (Number(cardAttr1) < 0
        || Number(cardAttr2 < 0)
        || Number(cardAttr3 < 0)) {
      result = true;
    }

    this.setState({ isSaveButtonDisabled: result });
  }

  onInputChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState(() => ({
      [name]: value }), () => {
      this.checkButton();
    });
  }

  // ref (https://newbedev.com/javascript-how-to-add-delete-button-in-react-js-code-example)
  delete(card) {
    const { deck } = this.state;
    const alou = deck.filter((c) => c.cardName !== card.cardName);
    this.setState({ deck: alou });
    const trunfo = alou.filter((c) => c.cardTrunfo);
    if (trunfo) this.setState({ hasTrunfo: false });
  }

  onCheckboxClick = (cards) => cards.filter(({ cardTrunfo }) => cardTrunfo === true)

  onNoCheckboxClick = (cards, filterName, filterRarity) => (
    cards.filter(({ cardName }) => (cardName.includes(filterName)))
      .filter(({ cardRare }) => (filterRarity === 'todas'
        ? (cardRare !== filterRarity) : (cardRare === filterRarity)))
  )

  cardsFiltered = () => {
    const { deck, filterName, filterRarity, filterCheckbox } = this.state;

    if (filterCheckbox) return this.onCheckboxClick(deck);
    if (filterRarity) return this.onNoCheckboxClick(deck, filterName, filterRarity);
  }


  render() {
 /*    const {
      deck,
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,  
      cardRare,
      cardTrunfo,
      hasTrunfo,
      isSaveButtonDisabled,
      filterCheckbox,
    } = this.state; */
    return (
      <div>
        <h1>Tryunfo</h1>
        <Form
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          hasTrunfo={ hasTrunfo }
          isSaveButtonDisabled={ isSaveButtonDisabled }
          onSaveButtonClick={ this.onSaveButtonClick }
          onInputChange={ this.onInputChange }


        />

        <div className="card">
          <Card
            cardName={ cardName }
            cardDescription={ cardDescription }
            cardAttr1={ cardAttr1 }
            cardAttr2={ cardAttr2 }
            cardAttr3={ cardAttr3 }
            cardImage={ cardImage }
            cardRare={ cardRare }
            cardTrunfo={ cardTrunfo }

          />


          {this.cardsFiltered().map((card) => (
            <div key={ card.cardName }>
              <Card {... card}/>

              <button
                type="button"
                data-testid="delete-button"
                onClick={ this.delete.bind(this, card) }
              >
                exluir
              </button>
            </div>))}

        </div>
        <Filters
        filterCheckbox={ filterCheckbox }
        onInputChange ={ this.onInputChange } />
      </div>
    );
  }
}

export default App;
