import React from 'react';
import Form from './components/Form';
import Card from './components/Card';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      cardName: '',
      cardDescription: '',
      cardAttr1: '',
      cardAttr2: '',
      cardAttr3: '',
      cardImage: 'https://pbs.twimg.com/profile_images/1367255090843811840/68JvdCTf.jpg',
      cardRare: 'normal',
      cardTrunfo: false,
      /*     hasTrunfo: false, */
      isSaveButtonDisabled: true,
      deck: [],

    };
  }

  onSaveButtonClick= () =>{
    const savedCard = {
      name: this.state.cardName,
      description: this.state.cardDescription,
      att1: this.state.cardAttr1,
      att2: this.state.cardAttr2,
      att3: this.state.cardAttr3,
      img: this.state.cardImage,
      rarity: this.state.cardRare,
      trunfo: this.state.cardTrunfo,
    }

    this.setState(() =>  ({
      deck:[...this.state.deck, savedCard],
      cardName: '',
      cardDescription: '',
      cardAttr1: '',
      cardAttr2: '',
      cardAttr3: '',
      cardImage: 'https://pbs.twimg.com/profile_images/1367255090843811840/68JvdCTf.jpg',
      cardRare: 'normal',
      /*     hasTrunfo: false, */
    }))

  }

  checkButton = () => {
    const
      {
        cardName,
        cardDescription,
        cardImage,
        cardAttr1,
        cardAttr2,
        cardAttr3,
      } = this.state;

    let result = false;

    if (
      cardName.length === 0
      || cardDescription.length === 0
      || cardImage.length === 0
    ) {
      result = true;
    }

    const total = 210;
    if (Number(cardAttr1) + Number(cardAttr2) + Number(cardAttr3) > total) {
      result = true;
    }

    const max = 90;
    if (Number(cardAttr1) > max
        || Number(cardAttr2) > max
        || Number(cardAttr3) > max) {
      result = true;
    }

    if (Number(cardAttr1) < 0
        || Number(cardAttr2 < 0)
        || Number(cardAttr3 < 0)) {
      result = true;
    }

    this.setState({ isSaveButtonDisabled: result });
  }

  onInputChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState(() => ({
      [name]: value }), () => {
      this.checkButton();
    });
  }

  render() {
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      /*     hasTrunfo, */
      isSaveButtonDisabled,
      onSaveButtonClick,
    } = this.state;
    return (
      <div>
        <h1>Tryunfo</h1>
        <Form
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          /*  hasTrunfo={ hasTrunfo } */
          isSaveButtonDisabled={ isSaveButtonDisabled }
          onSaveButtonClick={ onSaveButtonClick }
          onInputChange={ this.onInputChange }
          onSaveButtonClick={this.onSaveButtonClick}
        />

        <div className="card">
          <Card
            cardName={ cardName }
            cardDescription={ cardDescription }
            cardAttr1={ cardAttr1 }
            cardAttr2={ cardAttr2 }
            cardAttr3={ cardAttr3 }
            cardImage={ cardImage }
            cardRare={ cardRare }
            cardTrunfo={ cardTrunfo }
          />
        </div>
      </div>
    );
  }
}

export default App;
