import React from 'react';
import Form from './components/Form';
import Card from './components/Card';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      cardName: '',
      cardDescription: '',
      cardAttr1: '',
      cardAttr2: '',
      cardAttr3: '',
      cardImage: 'https://pbs.twimg.com/profile_images/1367255090843811840/68JvdCTf.jpg',
      cardRare: 'normal',
      cardTrunfo: false,
      /*     hasTrunfo: false, */
      isSaveButtonDisabled: true,

    };
  }

  checkButton = () => {
    let result = true
    if(
      cardName.length > 0
      && cardDescription.length > 0
      && cardImage.length >0
      ) {
         result = true
      } else if(Number(cardAttr1) +  Number(cardAttr2) +  Number(cardAttr3) > 210){
          result = true
      }else if(Number(cardAttr1) > 90
        || Number(cardAttr2 > 90) 
        || Number(cardAttr3 > 90)){
          result = true
        }else if(Number(cardAttr1) < 0
        || Number(cardAttr2 < 0) 
        || Number(cardAttr3 < 0)){
          result = true
        }
        else{
          result = false
        }

        this.setState({ isSaveButtonDisabled: result })
  }


  onInputChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState(() => ({
      [name]: value }), () => {this.checkButton();
      }
      );
  }

  render() {
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      /*     hasTrunfo, */
      isSaveButtonDisabled,
      onSaveButtonClick,
    } = this.state;
    return (
      <div>
        <h1>Tryunfo</h1>
        <Form
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          /*  hasTrunfo={ hasTrunfo } */
          isSaveButtonDisabled={ isSaveButtonDisabled }
          onSaveButtonClick={ onSaveButtonClick }
          onInputChange={ this.onInputChange }
        />

        <div className="card">
          <Card
            cardName={ cardName }
            cardDescription={ cardDescription }
            cardAttr1={ cardAttr1 }
            cardAttr2={ cardAttr2 }
            cardAttr3={ cardAttr3 }
            cardImage={ cardImage }
            cardRare={ cardRare }
            cardTrunfo={ cardTrunfo }
          />
        </div>
      </div>
    );
  }
}

export default App;
